import React from "react";

const ReadSummary = ({ read }) => {
  const avgUserRating =
    read.reduce((acc, curr) => acc + curr.userRating, 0) / read.length || 0;

  return (
    <div>
      <h2>Books you read</h2>
      <p>{read.length} books</p>
      <p>{avgUserRating.toFixed(2)} average rating</p>
    </div>
  );
};

export default ReadSummary;
