// src/components/ReadSummary.js
export default function ReadSummary({ read }) {
  const totalBooks = read.length;
  return (
    <div className="mb-4">
      <h2 className="text-2xl font-bold">My Reading List</h2>
      <p className="text-lg">You have read {totalBooks} books</p>
    </div>
  );
}
