import { useEffect, useState } from "react";

export default function MyBookList() {
  const [bookList, setBookList] = useState([]);

  useEffect(() => {
    const savedBookList = JSON.parse(localStorage.getItem("bookList")) || [];
    setBookList(savedBookList);
  }, []);

  const handleRemoveBook = (id) => {
    const updatedBookList = bookList.filter((book) => book.id !== id);
    setBookList(updatedBookList);
    localStorage.setItem("bookList", JSON.stringify(updatedBookList));
  };

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">My Book List</h1>
      {bookList.length === 0 ? (
        <p>You have no books in your list. Start adding some!</p>
      ) : (
        <ul className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
          {bookList.map((book) => (
            <li
              key={book.id}
              className="relative p-4 bg-white rounded-lg shadow"
            >
              <img
                className="w-full h-48 object-cover rounded"
                src={
                  book.volumeInfo.imageLinks?.thumbnail ||
                  "path/to/placeholder/image.jpg"
                }
                alt={`${book.volumeInfo.title} cover`}
              />
              <div className="mt-2">
                <h3 className="text-lg font-semibold">
                  {book.volumeInfo.title || "No title available"}
                </h3>
                <p className="text-gray-600">
                  {book.volumeInfo.authors?.join(", ") ||
                    "No authors available"}
                </p>
              </div>
              <button
                onClick={() => handleRemoveBook(book.id)}
                className="absolute top-2 right-2 p-1 bg-red-600 text-white rounded-full"
                aria-label="Remove book"
              >
                âœ•
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
