export default function Search({ query, setQuery }) {
  return (
    <div className="flex justify-center mb-4">
      <input
        type="text"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        className="w-full max-w-md p-2 border rounded shadow"
        placeholder="Search for books..."
      />
    </div>
  );
}
