import { useEffect, useRef, useState } from "react";
import StarRating from "./components/StarRating";
import { useKey } from "./hooks/useKey";
import { useLocalStorageState } from "./hooks/useLocalStorageState";
import { useBooks } from "./hooks/useBooks";
import { useDebounce } from "./hooks/useDebounce";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";

export default function App() {
  return (
    <Router>
      <NavBar />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/my-book-list" element={<MyBookList />} />
      </Routes>
    </Router>
  );
}

function Home() {
  const [query, setQuery] = useState("");
  const [selectedId, setSelectedId] = useState(null);
  const debouncedQuery = useDebounce(query, 500);
  const { books, isLoading, error } = useBooks(debouncedQuery);
  const [read, setRead] = useLocalStorageState([], "read");
  const [recommendedBooks, setRecommendedBooks] = useState([]);

  useEffect(() => {
    async function fetchRecommendedBooks() {
      try {
        const res = await fetch(
          "https://www.googleapis.com/books/v1/volumes?q=subject:fiction&_=${timestamp}"
        );
        const data = await res.json();
        setRecommendedBooks(data.items || []);
      } catch (err) {
        console.error("Failed to fetch recommended books:", err);
      }
    }
    fetchRecommendedBooks();
  }, []);

  function handleSelectBook(id) {
    setSelectedId((selectedId) => (id === selectedId ? null : id));
  }

  function handleCloseBook() {
    setSelectedId(null);
  }

  function handleAddRead(book) {
    setRead((prevRead) => {
      const updatedRead = [...prevRead, book];
      localStorage.setItem("read", JSON.stringify(updatedRead));
      return updatedRead;
    });
  }

  function handleDeleteRead(id) {
    setRead((read) => read.filter((book) => book.id !== id));
  }

  useEffect(() => {
    const storedRead = JSON.parse(localStorage.getItem("read")) || [];
    setRead(storedRead);
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 p-4">
      <Search query={query} setQuery={setQuery} />
      <NumResults books={books} />
      <Main>
        <Box>
          {isLoading && <Loader />}
          {!isLoading && !error && (
            <>
              {books.length === 0 && query === "" ? (
                <BookList
                  books={recommendedBooks}
                  onSelectBook={handleSelectBook}
                />
              ) : (
                <BookList books={books} onSelectBook={handleSelectBook} />
              )}
            </>
          )}
          {error && <ErrorMessage message={error} />}
        </Box>
        {selectedId && (
          <BookOverlay
            selectedId={selectedId}
            onCloseBook={handleCloseBook}
            onAddRead={handleAddRead}
            read={read}
          />
        )}
      </Main>
    </div>
  );
}

function MyBookList() {
  const [read, setRead] = useLocalStorageState([], "read");

  function handleDeleteRead(id) {
    setRead((read) => read.filter((book) => book.id !== id));
  }

  return (
    <Main>
      <ReadSummary read={read} />
      <ReadList read={read} onDeleteRead={handleDeleteRead} />
    </Main>
  );
}

function Loader() {
  return <p className="text-center text-gray-600">Loading...</p>;
}

function ErrorMessage({ message }) {
  return (
    <p className="text-center text-red-600">
      <span>‚õîÔ∏è</span> {message}
    </p>
  );
}

function NavBar() {
  return (
    <nav className="flex items-center justify-between p-4 bg-blue-600 text-white shadow-md">
      <Logo />
      <div className="flex space-x-4">
        <Link to="/" className="nav-link">
          Home
        </Link>
        <Link to="/my-book-list" className="nav-link">
          My Book List
        </Link>
      </div>
    </nav>
  );
}

function Logo() {
  return (
    <div className="flex items-center">
      <span role="img" className="text-2xl">
        üìö
      </span>
      <h1 className="ml-2 text-xl font-bold">useBooks</h1>
    </div>
  );
}

function Search({ query, setQuery }) {
  const inputEl = useRef(null);

  useKey("Enter", function () {
    if (document.activeElement === inputEl.current) return;
    inputEl.current.focus();
    setQuery("");
  });

  return (
    <input
      className="w-full p-2 mb-4 border rounded focus:outline-none focus:ring-2 focus:ring-blue-600"
      type="text"
      placeholder="Search books..."
      value={query}
      onChange={(e) => setQuery(e.target.value)}
      ref={inputEl}
    />
  );
}

function NumResults({ books }) {
  return (
    <p className="mb-4 text-lg">
      Found <strong>{books.length}</strong> results
    </p>
  );
}

function Main({ children }) {
  return <main className="bg-white p-4 rounded shadow-lg">{children}</main>;
}

function Box({ children }) {
  const [isOpen, setIsOpen] = useState(true);

  return (
    <div className="mb-4">
      <button
        className="mb-2 text-xl text-blue-600 focus:outline-none"
        onClick={() => setIsOpen((open) => !open)}
      >
        {isOpen ? "‚Äì" : "+"}
      </button>
      {isOpen && children}
    </div>
  );
}

function BookList({ books, onSelectBook }) {
  return (
    <ul className="grid gap-4 sm:grid-cols-2 lg:grid-cols-3">
      {books?.map((book) => (
        <Book book={book} key={book.id} onSelectBook={onSelectBook} />
      ))}
    </ul>
  );
}

function Book({ book, onSelectBook }) {
  return (
    <li
      className="p-4 bg-white rounded shadow cursor-pointer hover:bg-gray-100 transition duration-200"
      onClick={() => onSelectBook(book.id)}
    >
      <img
        className="w-full h-48 object-cover rounded"
        src={book.volumeInfo.imageLinks?.thumbnail}
        alt={`${book.volumeInfo.title} cover`}
      />
      <h3 className="mt-2 text-lg font-semibold">{book.volumeInfo.title}</h3>
      <p className="text-gray-600">
        <span>üóì</span>
        <span>{book.volumeInfo.publishedDate}</span>
      </p>
    </li>
  );
}

function BookOverlay({ selectedId, onCloseBook, onAddRead, read }) {
  const [book, setBook] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [userRating, setUserRating] = useState(0);

  const countRef = useRef(0);

  useEffect(() => {
    if (userRating) countRef.current++;
  }, [userRating]);

  const isRead = read.some((book) => book.id === selectedId);
  const readUserRating =
    read.find((book) => book.id === selectedId)?.userRating || 0;

  const {
    title,
    publishedDate,
    imageLinks,
    pageCount,
    averageRating,
    description,
    authors,
  } = book.volumeInfo || {};

  function handleAdd() {
    const newReadBook = {
      id: selectedId,
      title,
      publishedDate,
      imageLinks,
      pageCount,
      averageRating: Number(averageRating),
      userRating,
      countRatingDecisions: countRef.current,
    };
    onAddRead(newReadBook);
    onCloseBook();
  }

  useEffect(() => {
    async function fetchBookDetails() {
      setIsLoading(true);
      const res = await fetch(
        `https://www.googleapis.com/books/v1/volumes/${selectedId}`
      );
      const data = await res.json();
      setBook(data);
      setIsLoading(false);
    }
    fetchBookDetails();
  }, [selectedId]);

  useEffect(() => {
    if (!title) return;
    document.title = `Book | ${title}`;
    return () => {
      document.title = "useBooks";
    };
  }, [title]);

  useKey("Escape", onCloseBook);

  return (
    <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
      <div className="bg-white p-4 rounded shadow-lg w-full max-w-3xl mx-4">
        {isLoading ? (
          <Loader />
        ) : (
          <div className="space-y-4">
            <header className="flex items-center justify-between">
              <h2 className="text-xl font-bold">{title}</h2>
              <button
                className="text-gray-600 hover:text-gray-900 transition duration-200"
                onClick={onCloseBook}
              >
                &times;
              </button>
            </header>
            <div className="flex space-x-4">
              <img
                className="w-1/3 h-auto rounded"
                src={imageLinks?.thumbnail}
                alt={`${title} cover`}
              />
              <div className="flex-1 space-y-2">
                <p className="text-gray-600">{description}</p>
                <p className="text-gray-600">
                  <span>üë©‚Äçüíª</span>
                  <span>{authors?.join(", ")}</span>
                </p>
                <p className="text-gray-600">
                  <span>üóì</span>
                  <span>{publishedDate}</span>
                </p>
                <p className="text-gray-600">
                  <span>‚è≥</span>
                  <span>{pageCount} pages</span>
                </p>
                <p className="text-gray-600">
                  <span>‚≠êÔ∏è</span>
                  <span>{averageRating || "N/A"}</span>
                </p>
                <div className="mt-4">
                  {!isRead ? (
                    <>
                      <StarRating
                        maxRating={5}
                        size={24}
                        onSetRating={setUserRating}
                      />
                      {userRating > 0 && (
                        <button
                          className="mt-2 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition duration-200"
                          onClick={handleAdd}
                        >
                          + Add to list
                        </button>
                      )}
                    </>
                  ) : (
                    <p>
                      You rated this book {readUserRating} ‚≠êÔ∏è (
                      {countRef.current} rating decisions)
                    </p>
                  )}
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

function ReadSummary({ read }) {
  const avgUserRating =
    read.reduce((acc, curr) => acc + curr.userRating, 0) / read.length;

  const avgBookRating =
    read.reduce((acc, curr) => acc + curr.averageRating, 0) / read.length;

  return (
    <div className="mb-4 p-4 bg-white rounded shadow-lg">
      <h2 className="text-xl font-bold">Books you read</h2>
      <div className="mt-2 flex space-x-4">
        <p>
          <span>#Ô∏è‚É£</span>
          <span>{read.length} books</span>
        </p>
        <p>
          <span>‚≠êÔ∏è</span>
          <span>{avgUserRating.toFixed(2)} average rating</span>
        </p>
        <p>
          <span>‚≠êÔ∏è</span>
          <span>{avgBookRating.toFixed(2)} average book rating</span>
        </p>
      </div>
    </div>
  );
}

function ReadList({ read, onDeleteRead }) {
  return (
    <ul className="space-y-4">
      {read.map((book) => (
        <ReadBook book={book} key={book.id} onDeleteRead={onDeleteRead} />
      ))}
    </ul>
  );
}

function ReadBook({ book, onDeleteRead }) {
  return (
    <li className="flex items-center justify-between p-4 bg-white rounded shadow">
      <img
        className="w-16 h-auto rounded"
        src={book.imageLinks?.thumbnail}
        alt={`${book.title} cover`}
      />
      <div className="flex-1 ml-4 space-y-1">
        <h3 className="text-lg font-semibold">{book.title}</h3>
        <p className="text-gray-600">
          <span>‚≠êÔ∏è</span>
          <span>{book.userRating}</span>
        </p>
        <p className="text-gray-600">
          <span>‚≠êÔ∏è</span>
          <span>{book.averageRating}</span>
        </p>
      </div>
      <button
        className="ml-4 text-red-600 hover:text-red-900 transition duration-200"
        onClick={() => onDeleteRead(book.id)}
      >
        &times;
      </button>
    </li>
  );
}
