import { useState } from "react";
import PropTypes from "prop-types";

const containerStyle = {
  display: "flex",
  alignItems: "center",
  gap: "16px",
};

const starContainerStyle = {
  display: "flex",
};

StarRating.propTypes = {
  maxRating: PropTypes.number,
  defaultRating: PropTypes.number,
  color: PropTypes.string,
  size: PropTypes.number,
  className: PropTypes.string,
  onSetRating: PropTypes.func,
};

export default function StarRating({
  maxRating = 5,
  defaultRating = 0,
  color = "#fcc419",
  size = 48,
  className = "",
  onSetRating,
}) {
  const [rating, setRating] = useState(defaultRating);
  const [tempRating, setTempRating] = useState(0);

  const textStyle = {
    lineHeight: "1",
    margin: "0",
    color,
    fontSize: `${size / 1.5}px`,
  };

  function handleRating(rating) {
    setRating(rating);
    onSetRating(rating);
  }

  return (
    <div style={containerStyle} className={className}>
      <div style={starContainerStyle}>
        {Array.from({ length: maxRating }, (_, i) => (
          <Star
            key={i}
            onRate={() => handleRating(i + 1)}
            full={tempRating ? tempRating >= i + 1 : rating >= i + 1}
            onHoverIn={() => setTempRating(i + 1)}
            onHoverOut={() => setTempRating(0)}
            color={color}
            size={size}
          />
        ))}
      </div>
      <p style={textStyle}>{tempRating || rating || ""}</p>
    </div>
  );
}

function Star({ onRate, full, onHoverIn, onHoverOut, color, size }) {
  const starStyle = {
    width: `${size}px`,
    height: `${size}px`,
    display: "block",
    cursor: "pointer",
  };

  return (
    <span
      role="button"
      style={starStyle}
      onClick={onRate}
      onMouseEnter={onHoverIn}
      onMouseLeave={onHoverOut}
    >
      {full ? (
        <svg
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 20 20"
          fill={color}
          stroke={color}
        >
          <path d="M9.049.927c.3-.921 1.603-.921 1.902 0l2.02 6.191h6.514c.969 0 1.371 1.24.588 1.81l-5.266 3.826 2.02 6.191c.3.921-.755 1.688-1.539 1.118L10 15.11l-5.266 3.826c-.784.57-1.838-.197-1.539-1.118l2.02-6.191-5.266-3.826c-.783-.57-.38-1.81.588-1.81h6.514L9.049.927z" />
        </svg>
      ) : (
        <svg
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 20 20"
          fill="none"
          stroke={color}
        >
          <path d="M9.049.927c.3-.921 1.603-.921 1.902 0l2.02 6.191h6.514c.969 0 1.371 1.24.588 1.81l-5.266 3.826 2.02 6.191c.3.921-.755 1.688-1.539 1.118L10 15.11l-5.266 3.826c-.784.57-1.838-.197-1.539-1.118l2.02-6.191-5.266-3.826c-.783-.57-.38-1.81.588-1.81h6.514L9.049.927z" />
        </svg>
      )}
    </span>
  );
}
