import Book from "./Book";

export default function BookList({ books, onSelectBook }) {
  return (
    <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
      {books.map((book) => (
        <Book key={book.id} book={book} onSelectBook={onSelectBook} />
      ))}
    </div>
  );
}
