import { useState } from "react";

export default function StarRating({
  maxRating,
  size,
  onSetRating,
  initialRating = 0,
  readOnly = false,
}) {
  const [rating, setRating] = useState(initialRating);

  const handleRating = (rate) => {
    if (!readOnly) {
      setRating(rate);
      onSetRating(rate);
    }
  };

  return (
    <div className="flex">
      {[...Array(maxRating)].map((_, index) => (
        <svg
          key={index}
          className={`w-${size} h-${size} cursor-pointer ${
            rating > index ? "text-yellow-500" : "text-gray-300"
          }`}
          fill="currentColor"
          onClick={() => handleRating(index + 1)}
          viewBox="0 0 24 24"
        >
          <path d="M12 .587l3.668 7.431 8.2 1.192-5.934 5.788 1.402 8.165L12 18.897l-7.336 3.866 1.402-8.165-5.934-5.788 8.2-1.192z" />
        </svg>
      ))}
    </div>
  );
}
