import { useEffect, useState, useRef } from "react";
import StarRating from "./components/StarRating";
import Loader from "./components/Loader";
import { useKey } from "./hooks/useKey";

export function BookList({ books, onSelectBook }) {
  return (
    <ul className="grid gap-4 sm:grid-cols-2 lg:grid-cols-3">
      {books?.map((book) => (
        <Book book={book} key={book.id} onSelectBook={onSelectBook} />
      ))}
    </ul>
  );
}

export function Book({ book, onSelectBook }) {
  return (
    <li
      className="p-4 bg-white rounded shadow cursor-pointer hover:bg-gray-100 transition duration-200"
      onClick={() => onSelectBook(book.id)}
    >
      <img
        className="w-full h-48 object-cover rounded"
        src={book.volumeInfo.imageLinks?.thumbnail}
        alt={`${book.volumeInfo.title} cover`}
      />
      <h3 className="mt-2 text-lg font-semibold">{book.volumeInfo.title}</h3>
      <p className="text-gray-600">
        <span>🗓</span>
        <span>{book.volumeInfo.publishedDate}</span>
      </p>
    </li>
  );
}

export function BookOverlay({ selectedId, onCloseBook, onAddRead, read }) {
  const [book, setBook] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [userRating, setUserRating] = useState(0);

  const countRef = useRef(0);

  useEffect(() => {
    if (userRating) countRef.current++;
  }, [userRating]);

  const isRead = read.some((book) => book.id === selectedId);
  const readUserRating =
    read.find((book) => book.id === selectedId)?.userRating || 0;

  const {
    title,
    publishedDate,
    imageLinks,
    pageCount,
    averageRating,
    description,
    authors,
  } = book.volumeInfo || {};

  function handleAdd() {
    const newReadBook = {
      id: selectedId,
      title,
      publishedDate,
      imageLinks,
      pageCount,
      averageRating: Number(averageRating),
      userRating,
      countRatingDecisions: countRef.current,
    };
    onAddRead(newReadBook);
    onCloseBook();
  }

  useEffect(() => {
    async function fetchBookDetails() {
      setIsLoading(true);
      const res = await fetch(
        `https://www.googleapis.com/books/v1/volumes/${selectedId}`
      );
      const data = await res.json();
      setBook(data);
      setIsLoading(false);
    }
    fetchBookDetails();
  }, [selectedId]);

  useEffect(() => {
    if (!title) return;
    document.title = `Book | ${title}`;
    return () => {
      document.title = "useBooks";
    };
  }, [title]);

  useKey("Escape", onCloseBook);

  return (
    <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
      <div className="bg-white p-4 rounded shadow-lg w-full max-w-3xl mx-4">
        {isLoading ? (
          <Loader />
        ) : (
          <div className="space-y-4">
            <header className="flex items-center justify-between">
              <h2 className="text-xl font-bold">{title}</h2>
              <button
                className="text-gray-600 hover:text-gray-900 transition duration-200"
                onClick={onCloseBook}
              >
                &times;
              </button>
            </header>
            <div className="flex space-x-4">
              <img
                className="w-1/3 h-auto rounded"
                src={imageLinks?.thumbnail}
                alt={`${title} cover`}
              />
              <div className="flex-1 space-y-2">
                <p className="text-gray-600">{description}</p>
                <p className="text-gray-600">
                  <span>👩‍💻</span>
                  <span>{authors?.join(", ")}</span>
                </p>
                <p className="text-gray-600">
                  <span>🗓</span>
                  <span>{publishedDate}</span>
                </p>
                <p className="text-gray-600">
                  <span>⏳</span>
                  <span>{pageCount} pages</span>
                </p>
                <p className="text-gray-600">
                  <span>⭐️</span>
                  <span>{averageRating || "N/A"}</span>
                </p>
                <div className="mt-4">
                  {!isRead ? (
                    <>
                      <StarRating
                        maxRating={5}
                        size={24}
                        onSetRating={setUserRating}
                      />
                      {userRating > 0 && (
                        <button
                          className="mt-2 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition duration-200"
                          onClick={handleAdd}
                        >
                          + Add to list
                        </button>
                      )}
                    </>
                  ) : (
                    <p>
                      You rated this book {readUserRating} ⭐️ (
                      {countRef.current} rating decisions)
                    </p>
                  )}
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export function ReadSummary({ read }) {
  const avgUserRating =
    read.reduce((acc, curr) => acc + curr.userRating, 0) / read.length;

  const avgBookRating =
    read.reduce((acc, curr) => acc + curr.averageRating, 0) / read.length;

  return (
    <div className="mb-4 p-4 bg-white rounded shadow-lg">
      <h2 className="text-xl font-bold">Books you read</h2>
      <div className="mt-2 flex space-x-4">
        <p>
          <span>#️⃣</span>
          <span>{read.length} books</span>
        </p>
        <p>
          <span>⭐️</span>
          <span>{avgUserRating.toFixed(2)} average rating</span>
        </p>
        <p>
          <span>⭐️</span>
          <span>{avgBookRating.toFixed(2)} average book rating</span>
        </p>
      </div>
    </div>
  );
}

export function ReadList({ read, onDeleteRead }) {
  return (
    <ul className="space-y-4">
      {read.map((book) => (
        <ReadBook book={book} key={book.id} onDeleteRead={onDeleteRead} />
      ))}
    </ul>
  );
}

export function ReadBook({ book, onDeleteRead }) {
  return (
    <li className="flex items-center justify-between p-4 bg-white rounded shadow">
      <img
        className="w-16 h-auto rounded"
        src={book.imageLinks?.thumbnail}
        alt={`${book.title} cover`}
      />
      <div className="flex-1 ml-4 space-y-1">
        <h3 className="text-lg font-semibold">{book.title}</h3>
        <p className="text-gray-600">
          <span>⭐️</span>
          <span>{book.userRating}</span>
        </p>
        <p className="text-gray-600">
          <span>⭐️</span>
          <span>{book.averageRating}</span>
        </p>
      </div>
      <button
        className="ml-4 text-red-600 hover:text-red-900 transition duration-200"
        onClick={() => onDeleteRead(book.id)}
      >
        &times;
      </button>
    </li>
  );
}
