export default function ReadBook({ book, onDeleteRead }) {
  return (
    <div className="bg-white p-4 rounded shadow">
      <h3 className="text-lg font-bold">{book.volumeInfo.title}</h3>
      <p className="text-gray-600">{book.volumeInfo.authors.join(", ")}</p>
      <p className="text-gray-700">{book.volumeInfo.description}</p>
      <button
        onClick={() => onDeleteRead(book.id)}
        className="mt-4 bg-red-600 text-white py-2 px-4 rounded"
      >
        Delete
      </button>
    </div>
  );
}
