// Search.js
import { useState } from "react";

export default function Search({ setQuery }) {
  const [filters, setFilters] = useState({ genre: "", author: "", date: "" });

  const handleChange = (e) => {
    setFilters({ ...filters, [e.target.name]: e.target.value });
  };

  const handleSearch = () => {
    let query = "intitle:";
    if (filters.genre) query += `+subject:${filters.genre}`;
    if (filters.author) query += `+inauthor:${filters.author}`;
    if (filters.date) query += `+inpublisher:${filters.date}`;
    setQuery(query);
  };

  return (
    <div className="p-4 bg-white rounded-lg shadow">
      <h2 className="text-xl font-semibold mb-4">Advanced Search</h2>
      <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
        <input
          type="text"
          name="genre"
          placeholder="Genre"
          value={filters.genre}
          onChange={handleChange}
          className="p-2 border rounded"
        />
        <input
          type="text"
          name="author"
          placeholder="Author"
          value={filters.author}
          onChange={handleChange}
          className="p-2 border rounded"
        />
        <input
          type="text"
          name="date"
          placeholder="Publication Date"
          value={filters.date}
          onChange={handleChange}
          className="p-2 border rounded"
        />
      </div>
      <button
        onClick={handleSearch}
        className="mt-4 p-2 bg-blue-600 text-white rounded"
      >
        Search
      </button>
    </div>
  );
}
