function Star({ onRate, full, onHoverIn, onHoverOut, color, size }) {
  const starStyle = {
    width: `${size}px`,
    height: `${size}px`,
    display: "block",
    cursor: "pointer",
    transition: "transform 0.2s ease-in-out",
  };

  return (
    <span
      role="button"
      style={starStyle}
      onClick={onRate}
      onMouseEnter={onHoverIn}
      onMouseLeave={onHoverOut}
      aria-label={full ? "star full" : "star empty"}
    >
      {full ? (
        <svg
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 20 20"
          fill={color}
          stroke={color}
        >
          <path d="M9.049.927c.3-.921 1.603-.921 1.902 0l2.02 6.191h6.514c.969 0 1.371 1.24.588 1.81l-5.266 3.826 2.02 6.191c.3.921-.755 1.688-1.539 1.118L10 15.11l-5.266 3.826c-.784.57-1.838-.197-1.539-1.118l2.02-6.191-5.266-3.826c-.783-.57-.38-1.81.588-1.81h6.514L9.049.927z" />
        </svg>
      ) : (
        <svg
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 20 20"
          fill="none"
          stroke={color}
        >
          <path d="M9.049.927c.3-.921 1.603-.921 1.902 0l2.02 6.191h6.514c.969 0 1.371 1.24.588 1.81l-5.266 3.826 2.02 6.191c.3.921-.755 1.688-1.539 1.118L10 15.11l-5.266 3.826c-.784.57-1.838-.197-1.539-1.118l2.02-6.191-5.266-3.826c-.783-.57-.38-1.81.588-1.81h6.514L9.049.927z" />
        </svg>
      )}
    </span>
  );
}

function StarRating({
  maxRating = 5,
  size = 20,
  color = "#FFD700",
  onSetRating,
  defaultRating = 0,
}) {
  const [rating, setRating] = useState(defaultRating);
  const [hover, setHover] = useState(0);

  function handleRating(newRating) {
    setRating(newRating);
    onSetRating(newRating);
  }

  return (
    <div
      style={{ display: "flex", alignItems: "center" }}
      onMouseLeave={() => setHover(0)}
    >
      {Array.from({ length: maxRating }, (_, i) => (
        <Star
          key={i}
          full={hover ? hover >= i + 1 : rating >= i + 1}
          onRate={() => handleRating(i + 1)}
          onHoverIn={() => setHover(i + 1)}
          onHoverOut={() => setHover(0)}
          color={color}
          size={size}
        />
      ))}
    </div>
  );
}
