import { useEffect, useState } from "react";

export default function MyBookList() {
  const [bookList, setBookList] = useState([]);

  useEffect(() => {
    const savedBookList = JSON.parse(localStorage.getItem("bookList")) || [];
    setBookList(savedBookList);
  }, []);

  const handleRemoveBook = (id) => {
    const updatedBookList = bookList.filter((book) => book.id !== id);
    setBookList(updatedBookList);
    localStorage.setItem("bookList", JSON.stringify(updatedBookList));
  };

  return (
    <div className="p-4 min-h-screen bg-gray-100">
      <h1 className="text-3xl font-bold mb-6 text-center">My Book List</h1>
      {bookList.length === 0 ? (
        <p className="text-center text-gray-600">
          You have no books in your list. Start adding some!
        </p>
      ) : (
        <ul className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {bookList.map((book) => (
            <li
              key={book.id}
              className="relative p-4 bg-white rounded-lg shadow-md transition-transform transform hover:-translate-y-1 hover:shadow-lg"
            >
              <img
                className="w-full h-48 object-cover rounded-t"
                src={
                  book.volumeInfo.imageLinks?.thumbnail ||
                  "path/to/placeholder/image.jpg"
                }
                alt={`${book.volumeInfo.title} cover`}
              />
              <div className="p-4">
                <h3 className="text-lg font-semibold text-gray-900">
                  {book.volumeInfo.title || "No title available"}
                </h3>
                <p className="text-gray-700">
                  {book.volumeInfo.authors?.join(", ") ||
                    "No authors available"}
                </p>
                <button
                  onClick={() => handleRemoveBook(book.id)}
                  className="absolute top-2 right-2 p-2 bg-red-500 text-white rounded-full hover:bg-red-600 focus:outline-none focus:ring-2 focus:ring-red-500 focus:ring-offset-2"
                  aria-label="Remove book"
                >
                  âœ•
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
