import { useState } from "react";

function StarRating({ maxRating = 5, size = 24, onSetRating }) {
  const [rating, setRating] = useState(0);

  function handleRating(rate) {
    setRating(rate);
    onSetRating(rate);
  }

  return (
    <div className="flex">
      {Array.from({ length: maxRating }, (_, index) => (
        <svg
          key={index}
          onClick={() => handleRating(index + 1)}
          xmlns="http://www.w3.org/2000/svg"
          fill={index < rating ? "yellow" : "gray"}
          viewBox="0 0 24 24"
          width={size}
          height={size}
          className="cursor-pointer"
        >
          <path d="M12 .587l3.668 7.57 8.332 1.151-6.064 5.826 1.424 8.22L12 18.896 5.64 23.354l1.424-8.22L1 9.308l8.332-1.151z" />
        </svg>
      ))}
    </div>
  );
}

export default StarRating;
