import { useEffect, useState, useRef } from "react";
import Loader from "./Loader";
import StarRating from "./StarRating";
import { useKey } from "../hooks/useKey";

export default function BookOverlay({
  selectedId,
  onCloseBook,
  onAddRead,
  read,
}) {
  const [book, setBook] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [userRating, setUserRating] = useState(0);

  const countRef = useRef(0);

  useEffect(() => {
    if (userRating) countRef.current++;
  }, [userRating]);

  const isRead = read.some((book) => book.id === selectedId);
  const readUserRating =
    read.find((book) => book.id === selectedId)?.userRating || 0;

  const {
    title,
    publishedDate,
    imageLinks,
    pageCount,
    averageRating,
    description,
    authors,
  } = book.volumeInfo || {};

  function handleAdd() {
    const newReadBook = {
      id: selectedId,
      title,
      publishedDate,
      imageLinks,
      pageCount,
      averageRating: Number(averageRating),
      userRating,
      countRatingDecisions: countRef.current,
    };

    onAddRead(newReadBook);
    onCloseBook();
  }

  useKey("Escape", onCloseBook);

  useEffect(() => {
    async function getBookDetails() {
      setIsLoading(true);
      const res = await fetch(
        `https://www.googleapis.com/books/v1/volumes/${selectedId}?key=${process.env.REACT_APP_GOOGLE_BOOKS_API_KEY}`
      );
      const data = await res.json();
      setBook(data);
      setIsLoading(false);
    }
    getBookDetails();
  }, [selectedId]);

  useEffect(() => {
    if (isRead) setUserRating(readUserRating);
  }, [isRead, readUserRating]);

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center p-4 bg-gray-800 bg-opacity-75">
      <div className="relative p-6 bg-white rounded shadow-lg w-11/12 sm:w-3/4 lg:w-2/3 xl:w-1/2">
        {isLoading ? (
          <Loader />
        ) : (
          <>
            <button
              className="absolute top-4 right-4 text-gray-600 hover:text-gray-900"
              onClick={onCloseBook}
            >
              &times;
            </button>
            <div className="flex flex-col sm:flex-row">
              <img
                className="w-full h-auto rounded sm:w-1/3"
                src={imageLinks?.thumbnail}
                alt={`${title} cover`}
              />
              <div className="mt-4 sm:mt-0 sm:ml-4">
                <h2 className="text-2xl font-bold">{title}</h2>
                <p className="mt-2 text-gray-600">{authors?.join(", ")}</p>
                <p className="mt-2 text-gray-600">{publishedDate}</p>
                <p className="mt-4">{description}</p>
                <p className="mt-4 text-gray-600">Page count: {pageCount}</p>
                <p className="mt-4 text-gray-600">
                  Average rating: {averageRating}
                </p>
                <StarRating
                  maxRating={5}
                  size={24}
                  onSetRating={setUserRating}
                  userRating={userRating}
                />
                {!isRead ? (
                  <button
                    className="mt-4 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition"
                    onClick={handleAdd}
                  >
                    Add to Read List
                  </button>
                ) : (
                  <p className="mt-4 text-green-600">Already in Read List</p>
                )}
              </div>
            </div>
          </>
        )}
      </div>
    </div>
  );
}
