import { useEffect, useRef, useState } from "react";
import StarRating from "./StarRating";
import { useKey } from "./useKey";
import { useLocalStorageState } from "./useLocalStorageState";
import { useBooks } from "./useBooks";
import { useDebounce } from "./useDebounce";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";

export default function App() {
  return (
    <Router>
      <NavBar />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/my-book-list" element={<MyBookList />} />
      </Routes>
    </Router>
  );
}

function Home() {
  const [query, setQuery] = useState("");
  const [selectedId, setSelectedId] = useState(null);
  const debouncedQuery = useDebounce(query, 500);
  const { books, isLoading, error } = useBooks(debouncedQuery);

  const [read, setRead] = useLocalStorageState([], "read");

  function handleSelectBook(id) {
    setSelectedId((selectedId) => (id === selectedId ? null : id));
  }

  function handleCloseBook() {
    setSelectedId(null);
  }

  function handleAddRead(book) {
    setRead((prevRead) => {
      const updatedRead = [...prevRead, book];
      localStorage.setItem("read", JSON.stringify(updatedRead));
      return updatedRead;
    });
  }

  function handleDeleteRead(id) {
    setRead((read) => read.filter((book) => book.id !== id));
  }

  useEffect(() => {
    const storedRead = JSON.parse(localStorage.getItem("read")) || [];
    setRead(storedRead);
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 p-4">
      <Search query={query} setQuery={setQuery} />
      <NumResults books={books} />

      <Main>
        <Box>
          {isLoading && <Loader />}
          {!isLoading && !error && (
            <BookList books={books} onSelectBook={handleSelectBook} />
          )}
          {error && <ErrorMessage message={error} />}
        </Box>

        {selectedId && (
          <BookOverlay
            selectedId={selectedId}
            onCloseBook={handleCloseBook}
            onAddRead={handleAddRead}
            read={read}
          />
        )}
      </Main>
    </div>
  );
}

function MyBookList() {
  const [read, setRead] = useLocalStorageState([], "read");

  function handleDeleteRead(id) {
    setRead((read) => read.filter((book) => book.id !== id));
  }

  return (
    <Main>
      <ReadSummary read={read} />
      <ReadList read={read} onDeleteRead={handleDeleteRead} />
    </Main>
  );
}

function Loader() {
  return <p className="text-center text-gray-600">Loading...</p>;
}

function ErrorMessage({ message }) {
  return (
    <p className="text-center text-red-600">
      <span>‚õîÔ∏è</span> {message}
    </p>
  );
}

function NavBar() {
  return (
    <nav className="flex items-center justify-between p-4 bg-blue-600 text-white">
      <Logo />
      <div>
        <Link to="/" className="nav-link">
          Home
        </Link>
        <Link to="/my-book-list" className="nav-link ml-4">
          My Book List
        </Link>
      </div>
    </nav>
  );
}

function Logo() {
  return (
    <div className="flex items-center">
      <span role="img" className="text-2xl">
        üìö
      </span>
      <h1 className="ml-2 text-xl font-bold">useBooks</h1>
    </div>
  );
}

function Search({ query, setQuery }) {
  const inputEl = useRef(null);

  useKey("Enter", function () {
    if (document.activeElement === inputEl.current) return;
    inputEl.current.focus();
    setQuery("");
  });

  return (
    <input
      className="w-full p-2 mb-4 border rounded"
      type="text"
      placeholder="Search books..."
      value={query}
      onChange={(e) => setQuery(e.target.value)}
      ref={inputEl}
    />
  );
}

function NumResults({ books }) {
  return (
    <p className="mb-4 text-lg">
      Found <strong>{books.length}</strong> results
    </p>
  );
}

function Main({ children }) {
  return <main className="bg-white p-4 rounded shadow">{children}</main>;
}

function Box({ children }) {
  const [isOpen, setIsOpen] = useState(true);

  return (
    <div className="mb-4">
      <button
        className="mb-2 text-xl text-blue-600 focus:outline-none"
        onClick={() => setIsOpen((open) => !open)}
      >
        {isOpen ? "‚Äì" : "+"}
      </button>

      {isOpen && children}
    </div>
  );
}

function BookList({ books, onSelectBook }) {
  return (
    <ul className="grid gap-4 sm:grid-cols-2 lg:grid-cols-3">
      {books?.map((book) => (
        <Book book={book} key={book.id} onSelectBook={onSelectBook} />
      ))}
    </ul>
  );
}

function Book({ book, onSelectBook }) {
  return (
    <li
      className="p-4 bg-white rounded shadow cursor-pointer hover:bg-gray-100"
      onClick={() => onSelectBook(book.id)}
    >
      <img
        className="w-full h-auto"
        src={book.volumeInfo.imageLinks?.thumbnail}
        alt={`${book.volumeInfo.title} cover`}
      />
      <h3 className="mt-2 text-lg font-semibold">{book.volumeInfo.title}</h3>
      <p className="text-gray-600">
        <span>üóì</span>
        <span>{book.volumeInfo.publishedDate}</span>
      </p>
    </li>
  );
}

function BookOverlay({ selectedId, onCloseBook, onAddRead, read }) {
  const [book, setBook] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [userRating, setUserRating] = useState(0);

  const countRef = useRef(0);

  useEffect(() => {
    if (userRating) countRef.current++;
  }, [userRating]);

  const isRead = read.some((book) => book.id === selectedId);
  const readUserRating =
    read.find((book) => book.id === selectedId)?.userRating || 0;

  const {
    title,
    publishedDate,
    imageLinks,
    pageCount,
    averageRating,
    description,
    authors,
  } = book.volumeInfo || {};

  function handleAdd() {
    const newReadBook = {
      id: selectedId,
      title,
      publishedDate,
      imageLinks,
      pageCount,
      averageRating: Number(averageRating),
      userRating,
      countRatingDecisions: countRef.current,
    };

    onAddRead(newReadBook);
    onCloseBook();
  }

  useKey("Escape", onCloseBook);

  useEffect(() => {
    async function getBookDetails() {
      setIsLoading(true);
      const res = await fetch(
        `https://www.googleapis.com/books/v1/volumes/${selectedId}?key=${process.env.REACT_APP_GOOGLE_BOOKS_API_KEY}`
      );
      const data = await res.json();
      setBook(data);
      setIsLoading(false);
    }
    getBookDetails();
  }, [selectedId]);

  useEffect(() => {
    if (isRead) setUserRating(readUserRating);
  }, [isRead, readUserRating]);

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center p-4 bg-gray-800 bg-opacity-75">
      <div className="relative p-6 bg-white rounded shadow-lg w-11/12 sm:w-3/4 lg:w-2/3 xl:w-1/2">
        {isLoading ? (
          <Loader />
        ) : (
          <>
            <button
              className="absolute top-4 right-4 text-gray-600 hover:text-gray-900"
              onClick={onCloseBook}
            >
              &times;
            </button>
            <div className="flex items-center mb-4">
              <img
                className="w-32 h-48"
                src={imageLinks?.thumbnail}
                alt={`${title} cover`}
              />
              <div className="ml-4">
                <h2 className="text-xl font-bold">{title}</h2>
                <p className="text-gray-600">{authors?.join(", ")}</p>
                <p className="text-gray-600">
                  {publishedDate} | {pageCount} pages
                </p>
                <p className="text-gray-600">Rating: {averageRating}</p>
              </div>
            </div>
            <p className="mb-4 text-gray-700">{description}</p>

            <div className="flex items-center mb-4">
              <StarRating
                maxRating={5}
                size={24}
                onSetRating={setUserRating}
                userRating={userRating}
              />
              {userRating > 0 && (
                <button
                  className="ml-4 px-4 py-2 text-white bg-blue-600 rounded hover:bg-blue-700"
                  onClick={handleAdd}
                >
                  {isRead ? "Update rating" : "Add to list"}
                </button>
              )}
            </div>
          </>
        )}
      </div>
    </div>
  );
}

function ReadSummary({ read }) {
  const avgUserRating =
    read.reduce((acc, cur) => acc + cur.userRating, 0) / read.length || 0;
  const avgBookRating =
    read.reduce((acc, cur) => acc + cur.averageRating, 0) / read.length || 0;

  return (
    <div className="p-4 mb-4 bg-white rounded shadow">
      <h2 className="text-xl font-bold">Books read</h2>
      <p>
        <span role="img" aria-label="books">
          üìö
        </span>{" "}
        {read.length} books
      </p>
      <p>
        <span role="img" aria-label="star">
          üåü
        </span>{" "}
        Average rating: {avgUserRating.toFixed(2)}
      </p>
      <p>
        <span role="img" aria-label="star">
          üåü
        </span>{" "}
        Average book rating: {avgBookRating.toFixed(2)}
      </p>
    </div>
  );
}

function ReadList({ read, onDeleteRead }) {
  return (
    <ul className="grid gap-4 sm:grid-cols-2 lg:grid-cols-3">
      {read.map((book) => (
        <ReadBook book={book} key={book.id} onDeleteRead={onDeleteRead} />
      ))}
    </ul>
  );
}

function ReadBook({ book, onDeleteRead }) {
  return (
    <li className="p-4 bg-white rounded shadow">
      <img
        className="w-full h-auto"
        src={book.imageLinks?.thumbnail}
        alt={`${book.title} cover`}
      />
      <h3 className="mt-2 text-lg font-semibold">{book.title}</h3>
      <p className="text-gray-600">
        <span role="img" aria-label="calendar">
          üóì
        </span>{" "}
        {book.publishedDate}
      </p>
      <p className="text-gray-600">Rating: {book.userRating}</p>
      <button
        className="mt-2 px-4 py-2 text-white bg-red-600 rounded hover:bg-red-700"
        onClick={() => onDeleteRead(book.id)}
      >
        Delete
      </button>
    </li>
  );
}
