import React, { useEffect, useState } from "react";
import StarRating from "./StarRating";
import { useKey } from "../hooks/useKey";

const BookOverlay = ({ selectedId, onCloseBook, onAddBook }) => {
  const [book, setBook] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [userRating, setUserRating] = useState(0);

  useEffect(() => {
    async function fetchBookDetails() {
      setIsLoading(true);
      const res = await fetch(
        `https://www.googleapis.com/books/v1/volumes/${selectedId}`
      );
      const data = await res.json();
      setBook(data);
      setIsLoading(false);
    }
    fetchBookDetails();
  }, [selectedId]);

  useKey("Escape", onCloseBook);

  const handleAdd = () => {
    const newBook = {
      id: selectedId,
      ...book.volumeInfo,
      userRating,
    };
    onAddBook(newBook);
    onCloseBook();
  };

  const { title, authors, publishedDate, description, imageLinks } =
    book.volumeInfo || {};

  return (
    <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
      <div className="bg-white p-4 rounded shadow-lg w-full max-w-3xl mx-4">
        {isLoading ? (
          <p>Loading...</p>
        ) : (
          <div>
            <header>
              <h2>{title}</h2>
              <button onClick={onCloseBook}>Close</button>
            </header>
            <div>
              <img src={imageLinks?.thumbnail} alt={`${title} cover`} />
              <div>
                <p>{description}</p>
                <p>{authors?.join(", ")}</p>
                <p>{publishedDate}</p>
                <StarRating maxRating={5} onSetRating={setUserRating} />
                <button onClick={handleAdd}>Add to My Book List</button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default BookOverlay;
