export default function ReadSummary({ read }) {
  const totalBooks = read.length;
  const averageRating =
    read.reduce((acc, book) => acc + (book.userRating || 0), 0) / totalBooks;

  return (
    <div className="p-4 mb-4 bg-white rounded shadow">
      <h2 className="text-lg font-semibold">Reading Summary</h2>
      <p>Total books read: {totalBooks}</p>
      <p>Average rating: {averageRating.toFixed(2)}</p>
    </div>
  );
}
