import React from "react";
import { useLocalStorageState } from "../hooks/useLocalStorageState";
import Book from "../components/Book";

const MyBookList = () => {
  const [read, setRead] = useLocalStorageState([], "read");

  function handleDeleteRead(id) {
    setRead((read) => read.filter((book) => book.id !== id));
  }

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">My Book List</h2>
      {read.length === 0 ? (
        <p className="text-center text-gray-600">No books in your list.</p>
      ) : (
        <ul className="grid gap-4 sm:grid-cols-2 lg:grid-cols-3">
          {read.map((book) => (
            <Book
              key={book.id}
              book={book}
              rating={book.userRating}
              onDelete={handleDeleteRead}
            />
          ))}
        </ul>
      )}
    </div>
  );
};

export default MyBookList;
