import { useNavigate } from "react-router-dom";
import StarRating from "./StarRating";

export default function Book({ book, rating }) {
  const navigate = useNavigate();
  const { volumeInfo } = book;
  const { title, imageLinks, publishedDate, authors } = volumeInfo;

  return (
    <li
      className="relative p-4 bg-white rounded-lg shadow-lg border-l-4 border-blue-500 hover:border-blue-600 transition-transform transform hover:-translate-y-1 hover:shadow-xl cursor-pointer"
      onClick={() => navigate(`/book/${book.id}`)}
    >
      <img
        className="w-full h-48 object-cover rounded-t-md"
        src={imageLinks?.thumbnail}
        alt={`${title} cover`}
      />
      <div className="p-4">
        <h3 className="text-xl font-bold text-gray-900">{title}</h3>
        <p className="text-gray-700">{authors?.join(", ")}</p>
        <p className="text-gray-700">Published: {publishedDate}</p>
        {rating && (
          <div className="flex items-center mt-2">
            <StarRating
              maxRating={5}
              size={24}
              onSetRating={() => {}}
              initialRating={rating}
              readOnly
            />
            <span className="ml-2 text-gray-700">{rating}</span>
          </div>
        )}
      </div>
    </li>
  );
}
