// src/components/BookOverlay.js
import { useEffect, useState } from "react";
import Loader from "./Loader";

export default function BookOverlay({
  selectedId,
  onCloseBook,
  onAddRead,
  read,
}) {
  const [book, setBook] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    async function fetchBook() {
      try {
        const res = await fetch(
          `https://www.googleapis.com/books/v1/volumes/${selectedId}`
        );
        const data = await res.json();
        setBook(data);
        setIsLoading(false);
      } catch (err) {
        console.error("Failed to fetch book:", err);
      }
    }

    fetchBook();
  }, [selectedId]);

  function handleAddRead() {
    onAddRead(book);
    onCloseBook();
  }

  if (isLoading) return <Loader />;
  if (!book) return null;

  return (
    <div className="fixed inset-0 bg-gray-800 bg-opacity-75 flex items-center justify-center p-4">
      <div className="bg-white p-4 rounded shadow-lg w-full max-w-md">
        <button
          className="mb-2 text-xl focus:outline-none"
          onClick={onCloseBook}
        >
          âœ–
        </button>
        <h2 className="text-2xl font-bold mb-4">{book.volumeInfo.title}</h2>
        <p className="mb-4">{book.volumeInfo.description}</p>
        <button
          className="bg-blue-600 text-white py-2 px-4 rounded focus:outline-none"
          onClick={handleAddRead}
        >
          Mark as Read
        </button>
      </div>
    </div>
  );
}
