// src/components/ReadBook.js
export default function ReadBook({ book, onDeleteRead }) {
  return (
    <div className="p-4 bg-white rounded shadow-md">
      <h3 className="text-lg font-bold">{book.volumeInfo.title}</h3>
      <button
        className="mt-2 text-red-600"
        onClick={() => onDeleteRead(book.id)}
      >
        Delete
      </button>
    </div>
  );
}
