import { useEffect, useRef, useState } from "react";
import StarRating from "./StarRating";
import { useKey } from "./useKey";
import { useLocalStorageState } from "./useLocalStorageState";
import { useBooks } from "./useBooks"; // Updated hook to fetch books
import { useDebounce } from "./useDebounce";

const KEY = "AIzaSyAqhLmHQd9t2G63hqEwmEst8uGmjG4JgdQ";

export default function App() {
  const [query, setQuery] = useState("");
  const [selectedId, setSelectedId] = useState(null);
  const debouncedQuery = useDebounce(query, 500);
  const { books, isLoading, error } = useBooks(debouncedQuery); // Updated to use books

  const [read, setRead] = useLocalStorageState([], "read");

  function handleSelectBook(id) {
    setSelectedId((selectedId) => (id === selectedId ? null : id));
  }

  function handleCloseBook() {
    setSelectedId(null);
  }

  function handleAddRead(book) {
    setRead((prevRead) => {
      const updatedRead = [...prevRead, book];
      localStorage.setItem("read", JSON.stringify(updatedRead));
      return updatedRead;
    });
  }

  function handleDeleteRead(id) {
    setRead((read) => read.filter((book) => book.id !== id));
  }

  useEffect(() => {
    const storedRead = JSON.parse(localStorage.getItem("read")) || [];
    setRead(storedRead);
  }, []);

  return (
    <>
      <NavBar>
        <Search query={query} setQuery={setQuery} />
        <NumResults books={books} />
      </NavBar>

      <Main>
        <Box>
          {isLoading && <Loader />}
          {!isLoading && !error && (
            <BookList books={books} onSelectBook={handleSelectBook} />
          )}
          {error && <ErrorMessage message={error} />}
        </Box>

        {selectedId && (
          <BookOverlay
            selectedId={selectedId}
            onCloseBook={handleCloseBook}
            onAddRead={handleAddRead}
            read={read}
          />
        )}
      </Main>
    </>
  );
}

function Loader() {
  return <p className="loader">Loading...</p>;
}

function ErrorMessage({ message }) {
  return (
    <p className="error">
      <span>⛔️</span> {message}
    </p>
  );
}

function NavBar({ children }) {
  return (
    <nav className="nav-bar">
      <Logo />
      {children}
    </nav>
  );
}

function Logo() {
  return (
    <div className="logo">
      <span role="img">📚</span>
      <h1>useBooks</h1>
    </div>
  );
}

function Search({ query, setQuery }) {
  const inputEl = useRef(null);

  useKey("Enter", function () {
    if (document.activeElement === inputEl.current) return;
    inputEl.current.focus();
    setQuery("");
  });

  return (
    <input
      className="search"
      type="text"
      placeholder="Search books..."
      value={query}
      onChange={(e) => setQuery(e.target.value)}
      ref={inputEl}
    />
  );
}

function NumResults({ books }) {
  return (
    <p className="num-results">
      Found <strong>{books.length}</strong> results
    </p>
  );
}

function Main({ children }) {
  return <main className="main">{children}</main>;
}

function Box({ children }) {
  const [isOpen, setIsOpen] = useState(true);

  return (
    <div className="box">
      <button className="btn-toggle" onClick={() => setIsOpen((open) => !open)}>
        {isOpen ? "–" : "+"}
      </button>

      {isOpen && children}
    </div>
  );
}

function BookList({ books, onSelectBook }) {
  return (
    <ul className="list list-books">
      {books?.map((book) => (
        <Book book={book} key={book.id} onSelectBook={onSelectBook} />
      ))}
    </ul>
  );
}

function Book({ book, onSelectBook }) {
  return (
    <li onClick={() => onSelectBook(book.id)}>
      <img
        src={book.volumeInfo.imageLinks?.thumbnail}
        alt={`${book.volumeInfo.title} cover`}
      />
      <h3>{book.volumeInfo.title}</h3>
      <div>
        <p>
          <span>🗓</span>
          <span>{book.volumeInfo.publishedDate}</span>
        </p>
      </div>
    </li>
  );
}

function BookOverlay({ selectedId, onCloseBook, onAddRead, read }) {
  const [book, setBook] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [userRating, setUserRating] = useState(0);

  const countRef = useRef(0);

  useEffect(() => {
    if (userRating) countRef.current++;
  }, [userRating]);

  const isRead = read.some((book) => book.id === selectedId);
  const readUserRating =
    read.find((book) => book.id === selectedId)?.userRating || 0;

  const {
    title,
    publishedDate,
    imageLinks,
    pageCount,
    averageRating,
    description,
    authors,
  } = book.volumeInfo || {};

  function handleAdd() {
    const newReadBook = {
      id: selectedId,
      title,
      publishedDate,
      imageLinks,
      pageCount,
      averageRating: Number(averageRating),
      userRating,
      countRatingDecisions: countRef.current,
    };

    onAddRead(newReadBook);
    onCloseBook();
  }

  useKey("Escape", onCloseBook);

  useEffect(() => {
    async function getBookDetails() {
      setIsLoading(true);
      const res = await fetch(
        `https://www.googleapis.com/books/v1/volumes/${selectedId}?key=${KEY}`
      );
      const data = await res.json();
      setBook(data);
      setIsLoading(false);
    }
    getBookDetails();
  }, [selectedId]);

  useEffect(() => {
    if (!title) return;
    document.title = `Book | ${title}`;

    return function () {
      document.title = "useBooks";
    };
  }, [title]);

  return (
    <div className="overlay">
      <div className="details">
        {isLoading ? (
          <Loader />
        ) : (
          <>
            <header>
              <button className="btn-back" onClick={onCloseBook}>
                &larr;
              </button>
              <img src={imageLinks?.thumbnail} alt={`Cover of ${title}`} />
              <div className="details-overview">
                <h2>{title}</h2>
                <p>
                  {publishedDate} &bull; {pageCount} pages
                </p>
                <p>
                  <span>⭐️</span>
                  {averageRating} average rating
                </p>
              </div>
            </header>

            <section>
              <div className="rating">
                {!isRead ? (
                  <>
                    <StarRating
                      maxRating={10}
                      size={24}
                      onSetRating={setUserRating}
                    />
                    {userRating > 0 && (
                      <button className="btn-add" onClick={handleAdd}>
                        + Add to list
                      </button>
                    )}
                  </>
                ) : (
                  <p>
                    You rated this book {readUserRating} <span>⭐️</span>
                  </p>
                )}
              </div>
              <p>
                <em>{description}</em>
              </p>
              <p>Authors: {authors?.join(", ")}</p>
            </section>
          </>
        )}
      </div>
    </div>
  );
}

function ReadSummary({ read }) {
  const avgRating = average(read.map((book) => book.averageRating));
  const avgUserRating = average(read.map((book) => book.userRating));
  const avgPageCount = average(read.map((book) => book.pageCount));

  return (
    <div className="summary">
      <h2>Books you read</h2>
      <div>
        <p>
          <span>#️⃣</span>
          <span>{read.length} books</span>
        </p>
        <p>
          <span>⭐️</span>
          <span>{avgRating.toFixed(2)}</span>
        </p>
        <p>
          <span>🌟</span>
          <span>{avgUserRating.toFixed(2)}</span>
        </p>
        <p>
          <span>📄</span>
          <span>{avgPageCount.toFixed(0)} pages</span>
        </p>
      </div>
    </div>
  );
}

function ReadList({ read, onDeleteRead }) {
  return (
    <ul className="list">
      {read.map((book) => (
        <ReadBook book={book} key={book.id} onDeleteRead={onDeleteRead} />
      ))}
    </ul>
  );
}

function ReadBook({ book, onDeleteRead }) {
  return (
    <li>
      <img src={book.imageLinks?.thumbnail} alt={`${book.title} cover`} />
      <h3>{book.title}</h3>
      <div>
        <p>
          <span>⭐️</span>
          <span>{book.userRating}</span>
        </p>
        <p>
          <span>🌟</span>
          <span>{book.averageRating}</span>
        </p>
        <button className="btn-delete" onClick={() => onDeleteRead(book.id)}>
          X
        </button>
      </div>
    </li>
  );
}

const average = (arr) =>
  arr.reduce((acc, cur, i, arr) => acc + cur / arr.length, 0);
