// components/BookItem.js
import { useNavigate } from "react-router-dom";

export default function BookItem({ book, onDeleteRead, onSelectBook }) {
  const navigate = useNavigate();
  const { volumeInfo } = book;
  const { title, imageLinks, publishedDate, authors } = volumeInfo;

  return (
    <li
      className="flex items-center justify-between p-4 bg-white rounded shadow cursor-pointer"
      onClick={() => {
        if (onSelectBook) {
          onSelectBook(book.id);
        } else {
          navigate(`/book/${book.id}`);
        }
      }}
    >
      <img
        className="w-16 h-auto rounded"
        src={imageLinks?.thumbnail || "undefined cover"}
        alt={`${title || "No title available"} cover`}
      />
      <div className="flex-1 ml-4 space-y-1">
        <h3 className="text-lg font-semibold">
          {title || "No title available"}
        </h3>
        <p className="text-gray-600">{authors?.join(", ")}</p>
        <p className="text-gray-600">Published: {publishedDate}</p>
      </div>
      {onDeleteRead && (
        <button
          className="ml-4 text-red-600 hover:text-red-900 transition duration-200"
          onClick={(e) => {
            e.stopPropagation();
            onDeleteRead(book.id);
          }}
        >
          &times;
        </button>
      )}
    </li>
  );
}
