import React from "react";

const BookOverlay = ({ book, onClose }) => (
  <div className="overlay">
    <button onClick={onClose}>Close</button>
    <h2>{book.title}</h2>
    <p>{book.description}</p>
  </div>
);

export default BookOverlay;
