import React, { useContext } from "react";
import { useLocalStorageState } from "../hooks/useLocalStorageState";
import ReadSummary from "../components/ReadSummary";
import ReadList from "../components/ReadList";
import { FavoritesContext } from "../components/FavoritesContext";
import DarkModeToggle from "../components/DarkModeToggle";

export default function MyBookList() {
  const { favorites } = useContext(FavoritesContext);
  const [read, setRead] = useLocalStorageState([], "read");

  function handleDeleteRead(id) {
    setRead((read) => read.filter((book) => book.id !== id));
  }

  return (
    <div className="p-4">
      <header className="flex justify-between items-center mb-4">
        <h1 className="text-2xl font-bold">My Favorite Books</h1>
        <DarkModeToggle />
      </header>
      <ul className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
        {favorites.map((book) => (
          <Book key={book.id} book={book} />
        ))}
      </ul>
      <Main>
        <ReadSummary read={read} />
        <ReadList read={read} onDeleteRead={handleDeleteRead} />
      </Main>
    </div>
  );
}
