import { useState } from "react";
import { useLocalStorageState } from "../hooks/useLocalStorageState";
import ReadSummary from "../components/ReadSummary";
import ReadList from "../components/ReadList";
import BookOverlay from "../components/BookOverlay";

export default function MyBookList() {
  const [read, setRead] = useLocalStorageState([], "read");
  const [selectedId, setSelectedId] = useState(null);

  function handleDeleteRead(id) {
    setRead((read) => read.filter((book) => book.id !== id));
  }

  function handleSelectBook(id) {
    setSelectedId(id);
  }

  function handleCloseBook() {
    setSelectedId(null);
  }

  return (
    <div className="min-h-screen bg-gray-100 p-4">
      <ReadSummary read={read} />
      <ReadList
        read={read}
        onDeleteRead={handleDeleteRead}
        onSelectBook={handleSelectBook}
      />
      {selectedId && (
        <BookOverlay
          selectedId={selectedId}
          onCloseBook={handleCloseBook}
          onAddRead={() => {}} // No need to add read from this page
          read={read}
        />
      )}
    </div>
  );
}
