export default function ReadBook({ book, onDeleteRead, onSelectBook }) {
  return (
    <li
      className="flex items-center justify-between p-4 bg-white rounded shadow cursor-pointer"
      onClick={() => onSelectBook(book.id)}
    >
      <img
        className="w-16 h-auto rounded"
        src={book.volumeInfo.imageLinks?.thumbnail || "undefined cover"}
        alt={`${book.volumeInfo.title || "No title available"} cover`}
      />
      <div className="flex-1 ml-4 space-y-1">
        <h3 className="text-lg font-semibold">
          {book.volumeInfo.title || "No title available"}
        </h3>
        <p className="text-gray-600">
          <span>⭐️</span>
          <span>{book.userRating || "No rating available"}</span>
        </p>
        <p className="text-gray-600">
          <span>⭐️</span>
          <span>
            {book.volumeInfo.averageRating || "No average rating available"}
          </span>
        </p>
      </div>
      <button
        className="ml-4 text-red-600 hover:text-red-900 transition duration-200"
        onClick={(e) => {
          e.stopPropagation();
          onDeleteRead(book.id);
        }}
      >
        &times;
      </button>
    </li>
  );
}
