import { useState, useEffect } from "react";

const StarRating = ({
  maxRating,
  size,
  onSetRating,
  initialRating,
  readOnly,
}) => {
  const [currentRating, setCurrentRating] = useState(initialRating || 0);

  useEffect(() => {
    setCurrentRating(initialRating);
  }, [initialRating]);

  const handleRating = (index) => {
    if (!readOnly) {
      setCurrentRating(index);
      onSetRating(index);
    }
  };

  return (
    <div className="flex">
      {Array.from({ length: maxRating }, (_, index) => (
        <svg
          key={index}
          onClick={() => handleRating(index + 1)}
          className={`cursor-pointer w-${size} h-${size} ${
            index < currentRating ? "text-yellow-500" : "text-gray-300"
          }`}
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 24 24"
          fill="currentColor"
        >
          <path d="M12 17.27l5.18 3.73-1.64-5.81 4.46-3.88-5.82-.17L12 2 9.82 6.82l-5.82.17 4.46 3.88-1.64 5.81z" />
        </svg>
      ))}
    </div>
  );
};

export default StarRating;
