import React from "react";
import { useNavigate } from "react-router-dom";
import StarRating from "./StarRating";

export default function Book({ book, rating, onDelete }) {
  const navigate = useNavigate();
  const { volumeInfo } = book;
  const { title, imageLinks, publishedDate, authors } = volumeInfo;

  const handleRemoveBook = (id) => {
    onDelete(id);
  };

  return (
    <li
      className="relative p-4 bg-white rounded-lg shadow-lg border-l-4 border-blue-500 hover:border-blue-600 transition-transform transform hover:-translate-y-1 hover:shadow-xl"
      onClick={() => navigate(`/book/${book.id}`)}
    >
      <img
        className="w-full h-48 object-cover rounded-t-md"
        src={imageLinks?.thumbnail || "path/to/placeholder/image.jpg"}
        alt={`${title} cover`}
      />
      <div className="p-4">
        <h3 className="text-xl font-bold text-gray-900">
          {title || "No title available"}
        </h3>
        <p className="text-gray-700">
          {authors?.join(", ") || "No authors available"}
        </p>
        <button
          onClick={(e) => {
            e.stopPropagation();
            handleRemoveBook(book.id);
          }}
          className="absolute top-2 right-2 p-2 bg-red-500 text-white rounded-full hover:bg-red-600 focus:outline-none"
          aria-label="Remove book"
        >
          &times;
        </button>
        {rating && (
          <div className="flex items-center mt-2">
            <StarRating
              maxRating={5}
              size={24}
              onSetRating={() => {}}
              initialRating={rating}
              readOnly
            />
            <span className="ml-2 text-gray-700">{rating}</span>
          </div>
        )}
      </div>
    </li>
  );
}
