function ReadList({ read, onDeleteRead }) {
  return (
    <div className="flex flex-col space-y-4">
      {read.map((book) => (
        <div key={book.id} className="flex items-center space-x-4">
          <img
            className="w-16 h-24 object-cover"
            src={book.volumeInfo.imageLinks?.thumbnail}
            alt={`${book.volumeInfo.title} cover`}
          />
          <div className="flex-1">
            <h2 className="text-lg font-bold">{book.volumeInfo.title}</h2>
            <p className="text-gray-600">
              {book.volumeInfo.authors?.join(", ")}
            </p>
            <p className="text-gray-600">Rated: {book.userRating} stars</p>
          </div>
          <button
            className="bg-red-600 text-white px-4 py-2 rounded"
            onClick={() => onDeleteRead(book.id)}
          >
            Remove
          </button>
        </div>
      ))}
    </div>
  );
}

export default ReadList;
