// BookDetails.js
import { useParams, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import Loader from "../components/Loader";
import ErrorMessage from "../components/ErrorMessage";

export default function BookDetails() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [book, setBook] = useState(null);
  const [bookList, setBookList] = useState(
    () => JSON.parse(localStorage.getItem("bookList")) || []
  );
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    async function fetchBook() {
      setIsLoading(true);
      try {
        const res = await fetch(
          `https://www.googleapis.com/books/v1/volumes/${id}?key=${process.env.REACT_APP_GOOGLE_BOOKS_API_KEY}`
        );
        const data = await res.json();
        setBook(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setIsLoading(false);
      }
    }

    fetchBook();
  }, [id]);

  const addToBookList = () => {
    const newList = [...bookList, book];
    setBookList(newList);
    localStorage.setItem("bookList", JSON.stringify(newList));
  };

  if (isLoading) return <Loader />;
  if (error) return <ErrorMessage message={error} />;
  if (!book) return null;

  const {
    title,
    authors,
    publishedDate,
    imageLinks,
    pageCount,
    averageRating,
    description,
  } = book.volumeInfo;

  return (
    <div className="p-4 bg-white rounded-lg shadow-lg">
      <button
        className="mb-4 px-4 py-2 bg-blue-600 text-white rounded"
        onClick={() => navigate(-1)}
      >
        Back
      </button>
      <div className="flex flex-col sm:flex-row">
        <img
          className="w-full h-auto rounded sm:w-1/3"
          src={imageLinks?.thumbnail}
          alt={`${title} cover`}
        />
        <div className="mt-4 sm:mt-0 sm:ml-4">
          <h2 className="text-2xl font-bold">{title}</h2>
          <p className="mt-2 text-gray-600">{authors?.join(", ")}</p>
          <p className="mt-2 text-gray-600">{publishedDate}</p>
          <p className="mt-4">{description}</p>
          <p className="mt-4 text-gray-600">Page count: {pageCount}</p>
          <p className="mt-4 text-gray-600">Average rating: {averageRating}</p>
          <button
            className="mt-4 px-4 py-2 bg-green-600 text-white rounded"
            onClick={addToBookList}
          >
            Add to My Book List
          </button>
        </div>
      </div>
    </div>
  );
}
