export default function ReadSummary({ read }) {
  const numBooks = read.length;
  const averageRating =
    numBooks > 0
      ? (
          read.reduce((acc, book) => acc + book.volumeInfo.averageRating, 0) /
          numBooks
        ).toFixed(2)
      : 0;

  return (
    <div className="mb-4">
      <h2 className="text-xl font-bold">Read Summary</h2>
      <p className="text-gray-600">You have read {numBooks} books.</p>
      <p className="text-gray-600">Average Rating: {averageRating}</p>
    </div>
  );
}
