import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import StarRating from "./StarRating";

export default function Book({ book, onDelete }) {
  const navigate = useNavigate();
  const { volumeInfo } = book;
  const { title, imageLinks, publishedDate, authors } = volumeInfo;

  const [rating, setRating] = useState(() => {
    const storedRating = localStorage.getItem(`rating-${book.id}`);
    return storedRating ? parseInt(storedRating, 10) : 0;
  });

  const handleRatingChange = (newRating) => {
    setRating(newRating);
    localStorage.setItem(`rating-${book.id}`, newRating.toString());
  };

  useEffect(() => {
    const storedRating = localStorage.getItem(`rating-${book.id}`);
    if (storedRating) {
      setRating(parseInt(storedRating, 10));
    }
  }, [book.id]);

  return (
    <li
      className="relative p-4 bg-white rounded-lg shadow-lg border-l-4 border-blue-500 hover:border-blue-600 transition-transform transform hover:-translate-y-1 hover:shadow-xl cursor-pointer dark:bg-gray-800 dark:hover:bg-gray-700"
      onClick={() => navigate(`/book/${book.id}`)}
    >
      <button
        className="absolute top-2 right-2 text-red-500 hover:text-red-700 text-2xl p-2"
        onClick={(e) => {
          e.stopPropagation();
          onDelete(book.id);
        }}
      >
        &times;
      </button>
      <img
        className="w-full h-48 object-cover rounded-t-md"
        src={imageLinks?.thumbnail}
        alt={`${title} cover`}
      />
      <div className="p-4">
        <h3 className="text-xl font-bold text-gray-900 dark:text-gray-100">
          {title}
        </h3>
        <p className="text-gray-700 dark:text-gray-400">
          {authors?.join(", ")}
        </p>
        <p className="text-gray-700 dark:text-gray-400">
          Published: {publishedDate}
        </p>
        <div className="flex items-center mt-2">
          <StarRating
            maxRating={5}
            size={24}
            onSetRating={handleRatingChange}
            initialRating={rating}
          />
          <span className="ml-2 text-gray-700 dark:text-gray-400">
            {rating === 0 ? "Not rated" : `${rating} stars`}
          </span>
        </div>
      </div>
    </li>
  );
}
